package com.example.aleppocollage.connecterimport android.annotation.SuppressLintimport android.os.StrictModeimport android.util.Logimport java.sql.Connectionimport java.sql.DriverManagerimport java.sql.SQLExceptionimport javax.inject.Singletonclass ConnectDB() {    // Inst Alepp    private val database = "BakriInstitute"    private val server = "192.168.1.107:1433"    private val user = "mm"    private val password = "1234"    private var connection: Connection? = null    private var connectionString: String? = null    @SuppressLint("NewApi")    fun getConncetion(): Connection? {        val policy = StrictMode.ThreadPolicy.Builder().permitAll().build()        StrictMode.setThreadPolicy(policy)        try {            Class.forName("net.sourceforge.jtds.jdbc.Driver")            connectionString =                "jdbc:jtds:sqlserver://$server;databaseName=$database;user=$user;password=$password;"            connection = DriverManager.getConnection(connectionString)        } catch (se: SQLException) {            Log.e("ERRO", se.message!!)        } catch (e: ClassNotFoundException) {            Log.e("ERRO", e.message!!)        } catch (e: Exception) {            Log.e("ERRO", e.message!!)        }        return connection    }}