package com.example.aleppocollage.ui.auth.registerimport android.os.Buildimport android.os.Bundleimport android.view.Viewimport android.view.WindowManagerimport androidx.fragment.app.Fragmentimport androidx.fragment.app.viewModelsimport androidx.navigation.fragment.findNavControllerimport com.example.aleppocollage.ui.main.MainActivityimport com.example.aleppocollage.Rimport com.example.aleppocollage.databinding.FragmentRegsiterBindingclass RegisterFragment : Fragment(R.layout.fragment_regsiter) {    ///region ViewModel & Binding    private val viewModel: RegisterViewModel by viewModels()    private var _binding: FragmentRegsiterBinding? = null    private val binding get() = _binding!!    ///endregion    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        _binding = FragmentRegsiterBinding.bind(view)        if (Build.VERSION.SDK_INT >= 21) {            val window = (activity as MainActivity).window            window.addFlags(WindowManager.LayoutParams.FLAG_DRAWS_SYSTEM_BAR_BACKGROUNDS)            window.clearFlags(WindowManager.LayoutParams.FLAG_TRANSLUCENT_STATUS)            window.statusBarColor = this.resources.getColor(R.color.colorPrimary)        }        binding.apply {            btnRegisterStudent.setOnClickListener {                val action =                    RegisterFragmentDirections.actionNavigationRegisterToNavigationRegisterStudent()                findNavController().navigate(action)            }            btnRegisterTeacher.setOnClickListener {                val action =                    RegisterFragmentDirections.actionRegisterFragmentToRegisterTeacherFragment()                findNavController().navigate(action)            }        }    }    override fun onDestroyView() {        super.onDestroyView()        _binding = null    }}