package com.example.aleppocollage.ui.registerimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.Toastimport androidx.activity.OnBackPressedCallbackimport androidx.fragment.app.Fragmentimport androidx.lifecycle.ViewModelProviderimport androidx.navigation.fragment.findNavControllerimport com.example.aleppocollage.MainActivityimport com.example.aleppocollage.databinding.FragmentRegsiterBindingclass RegisterFragment : Fragment() {    private var _binding: FragmentRegsiterBinding? = null    private val binding get() = _binding!!    private lateinit var registerViewModel: RegisterViewModel    override fun onCreateView(        inflater: LayoutInflater,        container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        _binding = FragmentRegsiterBinding.inflate(inflater, container, false)        val view = binding.root        val callback: OnBackPressedCallback =            object : OnBackPressedCallback(true) {                override fun handleOnBackPressed() {                    (activity as MainActivity).finish()                }            }        registerViewModel = ViewModelProvider(this).get(RegisterViewModel::class.java)        binding.btnRegisterStudent.setOnClickListener {            val action =                RegisterFragmentDirections.actionNavigationRegisterToNavigationRegisterStudent()            findNavController().navigate(action)        }        binding.btnRegisterTeacher.setOnClickListener {            val action =                RegisterFragmentDirections.actionRegisterFragmentToRegisterTeacherFragment()            findNavController().navigate(action)        }        return view    }    override fun onDestroyView() {        super.onDestroyView()        _binding = null    }}